    public static TranslatingProtocolAdapter<ModbusItem, Object, ModbusSiemensSentron, ModbusSiemensRwSentron> 
        createConnectorAdapter(de.iip_ecosphere.platform.services.environment.metricsProvider.
        MetricsProvider metrics, File log, Supplier<String> inPathSupplier, Supplier<String> 
        outPathSupplier) {
        TranslatingProtocolAdapter<ModbusItem, Object, ModbusSiemensSentron, ModbusSiemensRwSentron> adapter;
        if (metrics != null) {
            adapter = new MonitoredTranslatingProtocolAdapter<ModbusItem, Object, ModbusSiemensSentron, 
                ModbusSiemensRwSentron>(new MachineOutputTranslator(outPathSupplier), new 
                MachineInputTranslator(inPathSupplier), metrics, log);
        } else {
            adapter = new TranslatingProtocolAdapter<ModbusItem, Object, ModbusSiemensSentron, ModbusSiemensRwSentron>(
                new MachineOutputTranslator(outPathSupplier), new MachineInputTranslator(
                inPathSupplier));
        }
        return adapter;
    }
