	@Inject
	public RtVilGrammarAccess(GrammarProvider grammarProvider,
		VilBuildLanguageGrammarAccess gaVilBuildLanguage,
		ExpressionDslGrammarAccess gaExpressionDsl) {
		this.grammar = internalFindGrammar(grammarProvider);
		this.gaVilBuildLanguage = gaVilBuildLanguage;
		this.gaExpressionDsl = gaExpressionDsl;
		this.pImplementationUnit = new ImplementationUnitElements();
		this.pLanguageUnit = new LanguageUnitElements();
		this.pRtContents = new RtContentsElements();
		this.pGlobalVariableDeclaration = new GlobalVariableDeclarationElements();
		this.pStrategyDeclaration = new StrategyDeclarationElements();
		this.pBreakdownElement = new BreakdownElementElements();
		this.pWeightingStatement = new WeightingStatementElements();
		this.pBreakdownStatement = new BreakdownStatementElements();
		this.pBreakdownWithPart = new BreakdownWithPartElements();
		this.pTacticDeclaration = new TacticDeclarationElements();
		this.pRuleElementBlock = new RuleElementBlockElements();
		this.pRuleElement = new RuleElementElements();
		this.pIntentDeclaration = new IntentDeclarationElements();
		this.pFailStatement = new FailStatementElements();
	}
	
	protected Grammar internalFindGrammar(GrammarProvider grammarProvider) {
		Grammar grammar = grammarProvider.getGrammar(this);
		while (grammar != null) {
			if ("de.uni_hildesheim.sse.vil.rt.RtVil".equals(grammar.getName())) {
				return grammar;
			}
			List<Grammar> grammars = grammar.getUsedGrammars();
			if (!grammars.isEmpty()) {
				grammar = grammars.iterator().next();
			} else {
				return null;
			}
		}
		return grammar;
	}
	
